import React, { useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [categoryInput, setCategoryInput] = useState('');
  const [categories, setCategories] = useState(['General']);
  const [dueDate, setDueDate] = useState(''); // New state for due date

  // Function to add a new to-do
  const addTodo = (e) => {
    e.preventDefault();
    if (input.trim()) {
      setTodos([
        ...todos,
        { text: input, completed: false, category: categoryInput || 'General', dueDate: dueDate || 'No due date' }
      ]);
      setInput('');
      setCategoryInput('');
      setDueDate(''); // Clear the due date input after adding
    }
  };

  // Function to add a new category
  const addCategory = (e) => {
    e.preventDefault();
    if (categoryInput.trim() && !categories.includes(categoryInput)) {
      setCategories([...categories, categoryInput]);
      setCategoryInput(''); 
    }
  };

  // Function to toggle task completion
  const toggleComplete = (index) => {
    const newTodos = todos.map((todo, i) => {
      if (i === index) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    });
    setTodos(newTodos);
  };

  // Function to delete a to-do
  const deleteTodo = (index) => {
    const newTodos = todos.filter((_, i) => i !== index);
    setTodos(newTodos);
  };

  const isOverdue = (dueDate) => {
    const currentDate = new Date();
    const taskDueDate = new Date(dueDate);
    return taskDueDate < currentDate && dueDate !== 'No due date';
  };  

  return (
    <div className="app">
      <h1>To-Do List App with Due Dates</h1>

      {/* Form to add a new category */}
      <form onSubmit={addCategory}>
        <input
          type="text"
          value={categoryInput}
          onChange={(e) => setCategoryInput(e.target.value)}
          placeholder="Add new category"
        />
        <button type="submit">Add Category</button>
      </form>

      {/* Form to add a new to-do */}
      <form onSubmit={addTodo}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Add a new task"
        />
        <select onChange={(e) => setCategoryInput(e.target.value)}>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        {/* Date picker for the due date */}
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <button type="submit">Add Task</button>
      </form>

      {/* Display list of to-dos */}
      <ul>
        {todos.map((todo, index) => (
          <li key={index} className={todo.completed ? 'completed' : ''}>
            <span onClick={() => toggleComplete(index)}>
              {todo.text} ({todo.category}) - Due: {todo.dueDate}
            </span>
            <button onClick={() => deleteTodo(index)}>Delete</button>
          </li>
          
        ))}
      </ul>
    </div>
  );
}

export default App;
